let renewAnimationRunning = false;

window.onscroll = () => {
    const scroll = window.scrollY;
    if (scroll >= 150) {
        let title = document.getElementById("title");
        title.style.opacity = "1.0";
        let opacity = 1.0;

        const frame = () => {
            if (window.scrollY >= 150) {
                if (opacity <= 0) {
                    clearInterval(id);
                } else {
                    opacity -= 0.1;
                    title.style.opacity = opacity;
                }
            } else {
                clearInterval(id)
            }
        }

        const id = setInterval(frame, 100);
    } else {
        let title = document.getElementById("title");
        if (title.style.opacity !== "1") {
            if (!renewAnimationRunning) {
                console.log("Starting restart animation...")
                let opacity = 0;

                const frame = () => {
                    if (opacity >= 1) {
                        title.style.opacity = "1";
                        renewAnimationRunning = false;
                        clearInterval(id);
                    } else {
                        opacity += 0.1;
                        console.log("Setting frame to " + opacity)
                        title.style.opacity = opacity;
                    }
                }

                renewAnimationRunning = true;
                const id = setInterval(frame, 100);
            }
        }
    }
}
