window.onscroll = () => {
    const scroll = window.scrollY;
    if (scroll >= 150) {
        let title = document.getElementById("title");
        title.style.opacity = "1.0";
        let opacity = 1.0;
        const id = setInterval(frame, 100);

        function frame() {
            if (window.scrollY >= 150) {
                if (opacity <= 0) {
                    clearInterval(id);
                } else {
                    opacity -= 0.1;
                    title.style.opacity = opacity;
                }
            } else {
                clearInterval(id)
            }
        }
    } else {
        let title = document.getElementById("title");
        if (title.style.opacity === "1") {
            return;
        }
        let opacity = 0;
        const id = setInterval(frame, 100);

        function frame() {
            if (window.scrollY >= 150) {
                if (opacity <= 0) {
                    clearInterval(id);
                } else {
                    opacity += 0.1;
                    title.style.opacity = opacity;
                }
            } else {
                clearInterval(id)
            }
        }

        title.style.opacity = "1.0";
    }
}
