window.onscroll = () => {
    const scroll = window.scrollY;
    if (scroll >= 150) {
        console.log("Higher that 150. Starting animation...")
        let title = document.getElementById("title");
        title.style.opacity = "1.0";
        let opacity = 1.0;
        const id = setInterval(frame, 100);
        function frame() {
            if (opacity <= 0) {
                clearInterval(id)
            } else {
                opacity -= 0.1;
                console.log("setting opacity to " + opacity);
                title.style.opacity = opacity;
            }
        }
    } else {
        console.log("Lower that 150")
        let title = document.getElementById("title");
        title.style.opacity = "1.0";
    }
}
