let titleAnimationRunning = false;
let oldScroll = 0;

window.onscroll = () => {
    const scroll = window.scrollY;

    console.log("Scroll to " + scroll)

    if (scroll >= 150) {
        let title = document.getElementById("title");

        if (!titleAnimationRunning) {
            //title.style.opacity = "1.0";
            let opacity = 1.0;

            if (scroll < oldScroll) {
                console.log("scroll < oldScroll " + scroll + " < " + oldScroll + ", " + (scroll < oldScroll))
            }

            const frame = () => {
                if (window.scrollY >= 150) {
                    if (opacity <= 0) {
                        title.style.opacity = "0";
                        console.log("Stopping main animation and setting opacity to " + title.style.opacity);
                        titleAnimationRunning = false;
                        clearInterval(id);
                    } else {
                        opacity -= 0.01;
                        title.style.opacity = opacity;
                    }
                } else {
                    console.log("Stopping main animation bc scroll... From main");
                    titleAnimationRunning = false;
                    clearInterval(id);
                }
            }

            console.log("Starting main animation...");
            titleAnimationRunning = true;
            const id = setInterval(frame, 10);
        }

    } else {
        let title = document.getElementById("title");

        if (!titleAnimationRunning) {
            console.log("Starting renew animation...");
            let opacity = 0;

            const frame = () => {
                if (opacity >= 1) {
                    console.log("Stopping renew animation...");
                    title.style.opacity = "1";
                    titleAnimationRunning = false;
                    clearInterval(id);
                } else {
                    opacity += 0.01;
                    title.style.opacity = opacity;
                }
            }

            titleAnimationRunning = true;
            const id = setInterval(frame, 10);
        }
    }

    oldScroll = scroll;
}
