let animationRunning = false;

window.onscroll = () => {
    const scroll = window.scrollY;
    console.log("Scroll to " + scroll)

    if (scroll >= 150) {
        if (!animationRunning) {
            let title = document.getElementById("title");
            //title.style.opacity = "1.0";
            let opacity = 1.0;

            const frame = () => {
                if (window.scrollY >= 150) {
                    if (opacity <= 0) {
                        title.style.opacity = "0";
                        console.log("Stopping main animation and setting opacity to " + title.style.opacity);
                        animationRunning = false;
                        clearInterval(id);
                    } else {
                        opacity -= 0.01;
                        console.log("Setting opacity to " + opacity);
                        title.style.opacity = opacity;
                    }
                } else {
                    console.log("Stopping main animation bc scroll");
                    animationRunning = false;
                    clearInterval(id);
                }
            }

            console.log("Starting main animation...");
            animationRunning = true;
            const id = setInterval(frame, 10);
        } else { console.log("Other animation is running... From main") }

    } else {
        console.log("got to else statement");
        let title = document.getElementById("title");

        if (parseFloat(title.style.opacity) < 1) {
            if (!animationRunning) {
                console.log("Starting restart animation...");
                let opacity = 0;

                const frame = () => {
                    if (opacity >= 1) {
                        console.log("Stopping animation...");
                        //title.style.opacity = "1";
                        animationRunning = false;
                        clearInterval(id);
                    } else {
                        opacity += 0.1;
                        console.log("Setting opacity to " + opacity)
                        title.style.opacity = opacity;
                    }
                }

                console.log("Current opacity: " + title.style.opacity);
                animationRunning = true;
                const id = setInterval(frame, 1000);
            } else { console.log("Other animation is running... From renew") }
        } else { console.log("Opacity is 1... From renew") }
    }
}
