window.onscroll = () => {
    const scroll = window.scrollY;
    if (scroll >= 100) {
        let title = document.getElementsByClassName("secondary-title");
        title.style.opacity = "1.0";
        let opacity = 1.0;
        const id = setInterval(frame, 10);
        function frame() {
            if (opacity === 0.1) {
                clearInterval(id)
            } else {
                opacity -= 0.1;
                title.style.opacity = opacity;
            }
        }
    } else {
        let title = document.getElementsByClassName("secondary-title");
        title.style.opacity = "1.0";
    }
}
