    let [broughtThings, setBroughtThings] = useState([]);

    addToBasket = (price, thing ) => {
        setBroughtThings(...broughtThings, [price, thing]);
        document.getElementById("basket-things-number").innerHTML = parseInt(document.getElementById(
            "basket-things-number").innerHTML
        ) + 1;
        console.log("Adding " + thing + " with price " + price);
    }

    getBasket = () => {
        return broughtThings;
    }

    return (
        <div className={ "basket-div" } onClick={ () => { window.location = "/basket"; } }>
            <p id={ "basket-things-number" } className={ "basket-things-number" } >0</p>
        </div>
    )
}


